<?phpfunction nombre_commentaire($x,$y){  $q = "select * from commentaire where nom='".$y."' and info='".$x."'  and etat !='s'";  if($z = mysql_query($q)){    $nb = mysql_num_rows($z);  }else{    $nb = 0;  }  return $nb;}function affiche_liste_commentaire($y,$x){   $temp = "";  $query = "select * from commentaire where nom='".$y."' and info='".$x."' and etat !='s' order by id DESC";  if(mysql_num_rows(mysql_query($query)) > 0){    $temp .= "<div class=\"comment\">\n";    $temp .= "<h1>Vos Commentaires</h1>\n";    if($res = mysql_query($query)){      while($ligne = mysql_fetch_array($res)){        $temp .= "<div>\n";            $p = photo("user",$_SESSION["userid"]);            if($p != ""){               $temp .= "<label id=\"marge\">";               $temp .= "<img src =\"".$p."\" style=\"height:36px;vertical-align:middle;padding:2px;border:1px solid #AAA;\">";               $temp .= "</label>";            }            $temp .= "<label id=\"marge\">";            $temp .= "<h6 style=\"margin:0;padding:0;\">";            $temp .= "Publi&eacute; le ".affiche_date_db($ligne["quand"]);            $temp .= "<br />".affiche_notation("etoile","commentaire",$ligne["id"],"");            $temp .= "</h6>";            $temp .= "<p style=\"margin:0;padding:0;\"><i>".cherche_signature($ligne["qui"])."</i></p>";            $temp .= "</label>";            $temp .= "<p id=\"juste\">";        $temp .= "".decode_html($ligne["data"])."";            $temp .= "</p>\n";        $temp .= "<p id=\"droite\">";        if(est_administrateur($_SESSION["userid"]) or $ligne["qui"] == $_SESSION["userid"]){          if(@$_SESSION["js"]=="0"){            $temp .= "<a href=\"".$_SESSION["index"]."?commentaire=".$ligne["id"]."\" title=\"Modifier ce commentaire\"><img src=\"images/boutons/16/R026.png\"></a>";          }else{            $temp .= "<a href=\"javascript:voir('commentaire');Ajax_Load('commentaire','".$_SESSION["index"]."form/form.commentaire.php?commentaire=".$ligne["id"]."');\" title=\"Modifier ce commentaire\"><img src=\"images/boutons/16/R026.png\"></a>";          }          $temp .= "<a title=\"Supprimer ce commentaire\" href=\"".$_SESSION["index"]."?table=commentaire&action=kill&form_id=".$ligne["id"]."\"><img src=\"images/boutons/16/R010.png\"></a>";        }        $temp .= "</p>\n";        $temp .= "</div>\n";      }    }    $temp .= "</div>\n";  }   return $temp;}?>