<?phpfunction sharedcount($url){   //$url = ((!empty($_SERVER['HTTPS'])) ? "https://": "http://" ) . $_SERVER['SERVER_NAME'].$_SERVER['REQUEST_URI'];   @$json = file_get_contents("http://api.sharedcount.com/?url=" . rawurlencode($url));   @$counts = json_decode($json, true);   $somme = @$counts["Twitter"] + @$counts["Facebook"]["like_count"]+ @$counts["Facebook"]["share_count"] + @$counts["GooglePlusOne"] ;   //return "This page has " . $counts["Twitter"] ." tweets, " . $counts["Facebook"]["like_count"] . " likes, and ". $counts["GooglePlusOne"] . "+1's";   return $somme;}function parse_signed_request($signed_request, $secret) {  list($encoded_sig, $payload) = explode('.', $signed_request, 2);  // decode the data  $sig = base64_url_decode($encoded_sig);  $data = json_decode(base64_url_decode($payload), true);  if (strtoupper($data['algorithm']) !== 'HMAC-SHA256') {    error_log('Unknown algorithm. Expected HMAC-SHA256');    return null;  }  // check sig  $expected_sig = hash_hmac('sha256', $payload, $secret, $raw = true);  if ($sig !== $expected_sig) {    error_log('Bad Signed JSON signature!');    return null;  }  return $data;}?>