<?phpfunction page(){   alertes();  switch ($_SESSION["page"]){    case "accueil":      include("page.accueil.php");    break;    case "article":      include("page.article.php");    break;    case "document":      include("page.document.php");    break;    case "vote":      inclure("page.vote.php");    break;      case "video":      inclure("page.video.php");    break;    case "user.inscription":      include("form/form.user.inscription.php");    break;    case "user.secour":      include("func/func.user.secour.php");    break;    case "admin":      include("page.sys.admin.php");    break;    case "user":      include("page.user.php");    break;    default :      if(file_exists("page.".$_SESSION["page"].".php")){        include_once("page.".$_SESSION["page"].".php");      }else{        $_SESSION["page"]="accueil";        inclure("page.accueil.php");      }    break;  }}function affiche_partager($x,$y){   $temp = "";   $titre = "";   $query = "select * from ".$x." where id='".$y."' limit 1";   if($res = mysql_query($query)){      $ligne = mysql_fetch_array($res);      $titre = decode_html($ligne["nom"]);      $titre = tronque($titre,98)."\n";      $titre = urlencode($titre);   }else{      $titre = urlencode("Venez nous voir sur Neocratie.net\n");   }   $z = "ceci";  $lien = $_SESSION["index"]."?page=".$x."&".$x."=".$y;      $temp .= "<a title=\"Partager ".$z." sur Facebook\" target=\"facebook\" href=\"http://www.facebook.com/sharer.php?s=100&p[url]=".urlencode($lien)."&p[title]=".$titre."\"><img src=\"images/social/24/f_gris.png\" class=\"social\"></a>";      $temp .= "<a title=\"Partager ".$z." sur Twitter\" target=\"twitter\" href=\"http://twitter.com/share?url=".urlencode($lien)."&text=".$titre."\"><img src=\"images/social/24/t_gris.png\" class=\"social\"></a>";    $temp .= "<a title=\"Partager ".$z." sur Google+\" target=\"google\" href=\"https://plus.google.com/share?url=".urlencode($lien)."\"><img src=\"images/social/24/g_gris.png\" class=\"social\"></a>\n";   return $temp;}function lien_definition($x,$y){  echo $x."<a href=\"http://fr.wiktionary.org/wiki/".$y."\" title=\"D&eacute;finition Wikip&eacute;dia\" target=\"_blank\"><img src=\"images/png/def.png\" class=\"def\"></a>";}function decode_html($x){  $x = html_entity_decode($x, ENT_QUOTES, 'UTF-8');  return $x;}?>