<?phpif(est_utilisateur($_SESSION["userid"])&& $_SESSION["userid"]!="Visiteur"){  $_SESSION["page"]="accueil";}else{  if(isset($_SESSION["form_email_user"]) or isset($_SESSION["form_login_user"])){    if(isset($_SESSION["form_login_user"])){      $sql = "select * from user where login='".$_SESSION["form_login_user"]."'";    }else{      if(isset($_SESSION["form_email_user"])){        $sql = "select * from user where email='".$_SESSION["form_email_user"]."' and nom='".$_SESSION["form_nom_user"]."' and prenom='".$_SESSION["form_prenom_user"]."' ";      }    }    @$result = mysql_query($sql);        @$ligne = mysql_fetch_array($result);    if($ligne){      $x = motdepasse_generation();      //$liste = "id=''";            $liste = "motdepasse='".sha1($x)."'";      $_SESSION["email"]["message"] = "Vos informations."."\r\n";      $_SESSION["email"]["message"] .= "Veuillez conserver ce message."."\r\n";      $_SESSION["email"]["message"] .= "\r\n";         $_SESSION["email"]["email"] = $ligne["email"];      $_SESSION["email"]["message"] .= "Votre nouveau mot de passe : ".$x."\r\n";      $_SESSION["email"]["message"] .= "Votre identifiant : ".$ligne["login"]."\r\n";               $_SESSION["email"]["titre"] = "http://".getenv("HTTP_HOST")." (Vos informations de connexion)";                                  $sqlx = "update user set ".$liste." where id='".$ligne["id"]."'";      if(!$resultx = mysql_query($sqlx)){echo "<p>".mysql_errno()." : ".mysql_error()."<p>Erreur dans user_secour";}      email_envoyer();      $_SESSION["alerte"] .= "<br />Consultez vos Emails !";      unset($_SESSION["form_email_user"]);      unset($_SESSION["form_nom_user"]);      unset($_SESSION["form_prenom_user"]);      unset($_SESSION["form_login_user"]);      $_SESSION["page"]="accueil";      page();    }else{      $_SESSION["alerte"] .= "<p>Utilisateur inconnu !</p>";          affiche_form_secour();    }      }else{     affiche_form_secour();  }}function 