<?phpfunction nb_vote($x,$y,$cont){  $query = "select * from notation where ".$y."='".$x."' and etat != 's'";  if($z = mysql_query($query)){$nb = mysql_num_rows($z);}else{$nb = 0;}   $temp = $nb;  return $temp;}function affiche_nb_vote($x,$y,$cont){  $query = "select * from notation where ".$y."='".$x."' and etat != 's'";  if($z = mysql_query($query)){$nb = mysql_num_rows($z);}else{$nb = 0;}   $temp = $nb." Vote".pluriel($nb);  return $temp;}?>