<?phpecho "<h1>Les vid&eacute;os du jour</h1>\n";echo "<hr />";echo "<div id=\"centre\">";$query = "select * from datas where type='video' and etat !='s' order by nom asc limit 3";if(mysql_num_rows(mysql_query($query)) > 0){  $res = mysql_query($query);  while($ligne = mysql_fetch_array($res)){      echo "<label id=\"inoir\" style=\"margin:2px;\">";      echo "<label id=\"centre\" style=\"margin:2px;width:240px;overflow:hidden;\">";      $txt = decode_html($ligne["data"]);      $txt = @str_ireplace('480', '240', $txt);      $txt = @str_ireplace('270', '135', $txt);      $txt = @str_ireplace('720', '240', $txt);      $txt = @str_ireplace('405', '135', $txt);      echo $txt;      echo "</label></label>";  }}echo "</div>\n";?>