ION["userid"] && est_utilisateur($_SESSION["userid"])){$ok = true;}    if (est_administrateur($_SESSION["userid"])){$ok = true;}    if($ligne["etat"] != "v"){$ok = true;}    if($ok == true){      $com = nombre_commentaire($ligne["id"],"article");      echo "<div id=\"farticle\">\n";         echo "<div id=\"commentaire\" style=\"display:none;\"><div class=\"pad\" id=\"centre\"><img src=\"images/loader/ajax-loader.gif\"></div></div>\n";      echo "<h1>\n";         if (est_administrateur($_SESSION["userid"]) or $ligne["qui"]==$_SESSION["userid"]){           //echo "<a href=\"javascript:Ajax_Load('farticle','".$_SESSION["location"]."scripts/inc.formulaire.article.php');\" title=\"Modifier cet article\"><img src=\"images/boutons/24/R026.png\" style=\"margin-right:10px;\"></a>";               echo "<a href=\"".$_SESSION["index"]."?acte=edit&article=".$ligne["id"]."\" title=\"Modifier cet article\"><img src=\"images/boutons/24/R026.png\" style=\"margin-right:10px;\"></a>";         }         if (est_administrateur($_SESSION["userid"])){           echo "<a title=\"Supprimer cet article\" href=\"".$_SESSION["index"]."?table=article&action=sauve&form_id=".$ligne["id"]."&form_etat=s\"><img src=\"images/boutons/16/R010.png\" style=\"margin-right:10px;\"></a>";           if($ligne["etat"]!="v"){             echo "<a title=\"Bloquer cet article\" href=\"".$_SESSION["index"]."?table=article&action=sauve&form_id=".$ligne["id"]."&form_etat=v\"><img src=\"images/boutons/16/R009.png\" style=\"margin-right:10px;\"></a>";           }else{echo "<a title=\"Autoriser cet article\" href=\"".$_SESSION["index"]."?table=article&action=sauve&form_id=".$ligne["id"]."&form_etat=a\"><img src=\"images/boutons/16/B009.png\" style=\"margin-right:10px;\"></a>";}         }else{if($ligne["qui"]==$_SESSION["userid"]){echo "<a title=\"Supprimer cet article\" href=\"".$_SESSION["index"]."?table=article&action=sauve&form_id=".$ligne["id"]."&form_etat=s\"><img src=\"images/boutons/16/R010.png\" style=\"margin-right:10px;\"></a>";}}         echo decode_html($ligne["nom"]);         echo "</h1><hr />\n";         echo "<div id=\"droite\" class=\"pad\">\n";            echo "<label id=\"pad\">";            echo "<div id=\"droite\">[ <i>Publi&eacute; le ".affiche_date_db($ligne["quand"])."</i> ]</div>";         if($ligne["info"]!=""){echo "<div id=\"droite\">[ <i>".decode_html($ligne["info"])."</i> ]</div>";}            echo "</label>";            //echo affiche_note_texte($ligne["id"],"article","");            echo affiche_notation("vote","article",$ligne["id"],"");         echo "</div><hr />\n";    echo "<div id=\"centre\">".affiche_notation("etoile","article",$ligne["id"],"")."</div><hr />";    echo "<div id=\"marge\">";    echo decode_html($ligne["data"])."</div>\n";         echo "<div id=\"droite\">".affiche_partager("article",$ligne["id"])."</div>\n";         echo "<hr />";         echo "<div id=\"droite\">\n";            echo "<label>";            echo "<i>".cherche_signature($ligne["qui"])."</i>";            echo "</label>";            $p = photo("user",$_SESSION["userid"]);            if($p != ""){               echo "<label class=\"marge\">";               echo "<img src =\"".$p."\" style=\"height:36px;vertical-align:middle;padding:2px;border:1px solid #333;\">";               echo "</label>";            }         echo "</div>\n";         echo "<hr />";      //if (est_utilisateur($_SESSION["userid"])){        //if(@$_SESSION["js"]=="0"){            include("form/form.commentaire.php");            //}      //}         echo affiche_liste_commentaire("article",$ligne["id"]);         echo "</div>\n";    }    return true;  }else{    return false;  }}?>