<?phpecho "<fieldset><legend>Editeur d'article</legend>\n";echo "<div class=\"marge\">\n";?><form id="form_liste_article" enctype="multipart/form-data" method="post" action="<?php echo $_SESSION["index"];?>"><p><select name="e_article" style="width:500px;text-align:left;"><option value="">Choisir un article pour le modifier</option><option value="">Nouvel Article</option><option value="">-----</option><?php$sql = "select * from article";$result = mysql_query($sql);while ($ligne = mysql_fetch_array($result)){  if($ligne["id"] == @$_SESSION["e_article"]){$_SESSION["form_article"] = $ligne;}  echo "<option value=\"".$ligne["id"]."\"";  if(@$_SESSION["e_article"] == $ligne["id"]){echo " selected";}  echo ">".html_entity_decode($ligne["nom"], ENT_QUOTES, 'UTF-8')."</option>";}?></select><input src="images/png/16/ico_027.png" type="image" title="Editer" style="border:0;background-color:#fff;margin-left:10px;" /></p><hr style="margin:20px 0 10px 0;" /></form><form id="form_article" enctype="multipart/form-data" method="post" action="<?php echo $_SESSION["index"];?>">  <?php    if(@$_SESSION["form_article"]["id"] != ""){      echo "<a title=\"Supprimer\" href=\"".$_SESSION["location"]."?table=article&acte=kill&kill=".$_SESSION["form_article"]["id"]."\">";      echo "<img src=\"images/png/ico_015.png\" style=\"width:18px;height:18px;margin-right:10px;\">";      echo "</a>";    }  ?>  <p>Titre :</p>  <p><input style="width:500px;" name="form_nom" title="Titre" type="text" placeholder="Titre" value="<?php if(isset($_SESSION["form_article"]["nom"])){echo decode_html($_SESSION["form_article"]["nom"]);}?>" /></p>  <p>Infos :</p>  <p><input style="width:500px;" name="form_info" title="Informations" type="text" placeholder="Informations" value="<?php if(isset($_SESSION["form_article"]["info"])){echo decode_html($_SESSION["form_article"]["info"]);}?>" /></p>  <p>Sujet :</p>  <p><select style="width:500px;" name="form_sujet">  <option value="">Choisir un sujet</option>  <option value=" "> </option>  <?php  $sql = "select * from sujet";  $result = mysql_query($sql);  while ($ligne = mysql_fetch_array($result)){    echo "<option value=\"".$ligne["id"]."\"";    if(@$_SESSION["form_article"]["sujet"] == $ligne["id"]){echo " selected";}    echo ">".decode_html($ligne["nom"])."</option>";  }?>  </select></p>  <p><textarea style="width:500px;" name="form_data"><?php if(isset($_SESSION["form_article"]["data"])){echo decode_html($_SESSION["form_article"]["data"]);}?></textarea>  <p id="centre" class="marge"><input src="images/png/32/ico_027.png" type="image" title="Valider" style="border:0;background-color:#fff;" /></p>  <input name="form_id" type="hidden" value="<?php if(isset($_SESSION["form_article"]["id"])){echo @$_SESSION["form_article"]["id"];} ?>" />  <input name="form_etat" type="hidden" value=<?php if(isset($_SESSION["form_article"]["etat"])){echo @$_SESSION["form_article"]["etat"];} ?>"" />  <input name="form_qui" type="hidden" value="<?php echo $_SESSION["userid"]; ?>" />  <input name="form_quand" type="hidden" value="<?php echo date_heure_db(); ?>" />  <input name="table" type="hidden" value="article" />  <input name="acte" type="hidden" value="<?php if(isset($_SESSION["form_article"])){echo "sauve";}else{echo "nouveau";} ?>" /></form><script> CKEDITOR.replace( 'form_data' );</script><?phpecho "</div>\n";echo "</fieldset>\n";//unset($_SESSION["e_article"]);unset($_SESSION["form_article"]);?>