<?php  $query = "select * from user order by id ASC";  $tailleimage = 18;  echo "<fieldset><legend>Utilisateurs</legend>\n";  if($res = mysql_query($query)){    echo "<table style=\"border-collapse: separate; border-spacing: 1px;\">\n";    while($ligne = mysql_fetch_array($res)){            echo "<tr>\n";             if($ligne["id"] != "1"){        echo "<td id=\"bord\" style=\"vertical-align:middle;padding:5px;\">";        echo "<a href=\"".$_SESSION["location"]."?page=admin&acte=el&el=".$ligne["id"]."\" title=\"Supprimer\">";        echo "<img src=\"images/png/poubelle_188.png\" style=\"width:".$tailleimage."px;margin-right:30px;\">";        echo "</a>";        echo "</td>\n";                echo "<td id=\"bord\" style=\"vertical-align:middle;padding:5px;\">";        if ($ligne["type"]=="user"){          echo "<a href=\"".$_SESSION["location"]."?page=admin&acte=use&use=".$ligne["id"]."&usev=banni\" title=\"Bannir cet utilisateur\">";          echo "<img src=\"images/png/user_006.png\" style=\"width:".$tailleimage."px;\">";          echo "</a>";        }else{          echo "<a href=\"".$_SESSION["location"]."?page=admin&acte=use&use=".$ligne["id"]."&usev=user\" title=\"Autoriser cet utilisateur\">";          echo "<img src=\"images/png/user_005.png\" style=\"width:".$tailleimage."px;\">";          echo "</a>";        }        echo "</td>\n";                echo "<td id=\"bord\" style=\"vertical-align:middle;padding:5px;\">";        if($ligne["systeme"]=="A"){          echo "<a href=\"".$_SESSION["location"]."?page=admin&acte=adm&adm=".$ligne["id"]."&admv=U\" title=\"DÃ©sactiver les droits d'administration\">";          echo "<img src=\"images/png/user_006.png\" style=\"width:".$tailleimage."px;\">";          echo "</a>";        }else{          echo "<a href=\"".$_SESSION["location"]."?page=admin&acte=adm&adm=".$ligne["id"]."&admv=A\" title=\"Activer les droits d'administration\">";          echo "<img src=\"images/png/user_005.png\" style=\"width:".$tailleimage."px;\">";          echo "</a>";        }        echo "</td>\n";                    }else{        echo "<td id=\"bord\" colspan=\"3\" style=\"vertical-align:middle;padding:5px;\">";        echo "<img src=\"images/png/user_092.png\" style=\"width:".$tailleimage."px;\" title=\"Le compte du concepteur est inaccessible.\">";        echo "</td>\n";      }            echo "<td id=\"bord\" style=\"vertical-align:middle;padding:5px;\">";      $z = trouve_photo("user",$ligne["id"]);      if($z!= ""){        echo "<img style=\"height:".$tailleimage."px;border:0;\" src=\"".$_SESSION["location"].$z."\">";      }      echo "</td>\n";            echo "<td id=\"bord\" style=\"vertical-align:middle;padding:5px;\">";      if($ligne["email"]!= ""){        echo "<a href=\"mailto:".$ligne["email"]."\" title=\"Envoyer un courriel\"><img style=\"height:".$tailleimage."px;border:0;\" src=\"".$_SESSION["location"]."images/png/email_048.png"."\"></a>";      }      echo "</td>\n";            echo "<td id=\"bord\" style=\"vertical-align:middle;padding:5px;\">";      echo " <b>".cherche_civilite($ligne["civilite"])." ".$ligne["prenom"]." ".$ligne["nom"]."</b> ";      echo "</td>";      echo "</tr>\n";          }    echo "</table>\n";  }  echo "</fieldset>\n";?>