ESSION["userid"])){$ok = true;}    if (est_administrateur($_SESSION["userid"])){$ok = true;}    if($ligne["etat"] != "v"){$ok = true;}    if($ok == true){      $com = nombre_commentaire($ligne["id"],"vote");         echo "<div id=\"fvote\">\n";         echo "<h1>\n";         if (est_administrateur($_SESSION["userid"]) or $ligne["qui"]==$_SESSION["userid"]){           //echo "<a href=\"javascript:Ajax_Load('fvote','".$_SESSION["index"]."scripts/inc.formulaire.vote.php');\" title=\"Modifier ce vote\"><img src=\"images/boutons/24/R026.png\" style=\"margin-right:10px;\"></a>";               echo "<a href=\"".$_SESSION["index"]."?acte=edit&vote=".$ligne["id"]."\" title=\"Modifier ce vote\"><img src=\"images/boutons/24/R026.png\" style=\"margin-right:10px;\"></a>";            }         if (est_administrateur($_SESSION["userid"])){           echo "<a title=\"Supprimer ce vote\" href=\"".$_SESSION["index"]."?table=vote&action=sauve&form_id=".$ligne["id"]."&form_etat=s\"><img src=\"images/boutons/16/R010.png\" style=\"margin-right:10px;\"></a>";           if($ligne["etat"]!="v"){             echo "<a title=\"Bloquer ce vote\" href=\"".$_SESSION["index"]."?table=vote&action=sauve&form_id=".$ligne["id"]."&form_etat=v\"><img src=\"images/boutons/16/R009.png\" style=\"margin-right:10px;\"></a>";           }else{echo "<a title=\"Autoriser ce vote\" href=\"".$_SESSION["index"]."?table=vote&action=sauve&form_id=".$ligne["id"]."&form_etat=a\"><img src=\"images/boutons/16/B009.png\" style=\"margin-right:10px;\"></a>";}         }else{           if($ligne["qui"]==$_SESSION["userid"]){echo "<a title=\"Supprimer ce vote\" href=\"".$_SESSION["index"]."?table=vote&action=sauve&form_id=".$ligne["id"]."&form_etat=s\"><img src=\"images/boutons/16/R010.png\" style=\"margin-right:10px;\"></a>";}         }         echo decode_html($ligne["nom"]);         echo "</h1><hr />\n";         echo "<div id=\"droite\" class=\"pad\">\n";            echo "<label id=\"pad\">";     