<?phpmachine();include("sys.constantes.php");include("sys.fonctions.php");if(!isset($_SESSION["alerte"])){$_SESSION["alerte"] = "";}if(!isset($_SESSION["page"])){$_SESSION["page"] = "accueil";}if(!isset($_SESSION["userid"])){$_SESSION["userid"] = "Visiteur";}sql_connexion();login();switch (@$_SESSION["action"]) {  default :  break;   case "logout":      if(file_exists("func/func.user.logout.php")){include_once("func/func.user.logout.php");}   break;   case "inscription":     if(file_exists("func/func.user.inscription.php")){include_once("func/func.user.inscription.php");}   break;   case "":   break;   case "":   break;   case "":   break;  case "nouveau":      switch (@$_SESSION["table"]) {         case "commentaire":            if(@$_SESSION["form_data"] != ""){          if (!est_administrateur(@$_SESSION["userid"])){               $_SESSION["form_data"] = str_ireplace('<', '#', $_SESSION["form_data"]);               $_SESSION["form_data"] = str_ireplace('>', '#', $_SESSION["form_data"]);               $_SESSION["form_data"] = str_ireplace('www.', 'site ', $_SESSION["form_data"]);               $_SESSION["form_data"] = str_ireplace('script', '#', $_SESSION["form_data"]);               $_SESSION["form_data"] = str_ireplace('$', '#', $_SESSION["form_data"]);            $_SESSION["form_data"] = str_ireplace('href', '#', $_SESSION["form_data"]);            $_SESSION["form_data"] = str_ireplace('query', '#', $_SESSION["form_data"]);            //$_SESSION["form_data"] = str_ireplace('', '#', $_SESSION["form_data"]);          }               sauve_insert();            }         break;      default :            sauve_insert();      break;      }    session_form_unset();    unset($_SESSION["table"]);    $_SESSION["sujet"]="tous";  break;  case "sauve":    sauve_update();    session_form_unset();    unset($_SESSION["table"]);      $_SESSION["sujet"]="tous";  break;  case "kill":    $_SESSION["form_etat"]="s";    sauve_update();    unset($_SESSION["table"]);      session_form_unset();  break;  case "notation_v":    $_SESSION["table"] = "notation";    $_SESSION["form_nom"] = @$_SESSION["nom"];    $_SESSION["form_data"] = @$_SESSION["data"];    $_SESSION["form_info"] = @$_SESSION["page"];    $_SESSION["form_quand"] = date_heure_db();    if(est_utilisateur($_SESSION["userid"])){$_SESSION["form_qui"] = $_SESSION["userid"];}    $_SESSION["form_cible_type"] = @$_SESSION["type"];    $_SESSION["form_cible_id"] = @$_SESSION["tid"];    $_SESSION["form_ip"] = $_SESSION["userip"];    if(!user_notation(@$_SESSION["nom"],@$_SESSION["type"],@$_SESSION["tid"])){sauve_insert();}    echo affiche_notation(@$_SESSION["nom"],@$_SESSION["type"],@$_SESSION["tid"],"retour");    session_form_unset();    unset($_SESSION["table"]);    unset($_SESSION["nom"]);    unset($_SESSION["data"]);    unset($_SESSION["type"]);    unset($_SESSION["tid"]);    unset($_SESSION["action"]);  break;   case "sauve_user":   break;   case "upload_photo":      if(file_exists("func/func.upload.photo.php")){include("func/func.upload.photo.php");}   break;   case "sup_photo":      $img = array("jpg","png","gif","jpeg");    while (list($key, $val) = each($img)){      $x = "images/photos/".$_SESSION["img_type"]."_".$_SESSION["img_id"].".".$val;      if(file_exists($x)){unlink($x);}    }      unset($_SESSION["img_id"]);      unset($_SESSION["img_type"]);  break;}unset($_SESSION["action"]);?>