<?phpif(count($_GET)){  while (list($key, $val) = each($_GET)){    if($val!=""){      $_SESSION[$key]= htmlentities($val,ENT_QUOTES,'UTF-8');      //$_SESSION[$key]= $val;    }else{      unset($_SESSION[$key]);    }  }}if(count($_POST)){  while (list($key, $val) = each($_POST)){    if($val!=""){      $_SESSION[$key]= htmlentities($val,ENT_QUOTES,'UTF-8');      //$_SESSION[$key]= $val;    }else{      unset($_SESSION[$key]);    }  }}@date_default_timezone_set("Europe/Paris");function inclure($x){  if(file_exists($x)){    include_once($x);  }else{    if(file_exists("../".$x)){      include_once("../".$x);    }  }}function machine(){   if(!isset($_SESSION["machine"])){     $_SESSION["machine"] = getenv("HTTP_HOST");     $mac = @explode( '.', $_SESSION["machine"]);     if ($_SESSION["machine"] == "127.0.0.1" or $_SESSION["machine"] == "localhost"  or @$mac[0] == "192"){       $_SESSION["location"] = "http://".$_SESSION["machine"]."/";     } else {//       $_SESSION["location"] = "http://www.neocratie.net/";       $_SESSION["location"] = "http://".$_SESSION["machine"]."/";     }     $HTTP_REFERER = getenv("HTTP_REFERER");     if ( !mb_eregi($_SESSION["machine"],$HTTP_REFERER) ) {       if ($HTTP_REFERER == "") {$_SESSION["provenance"] = "Favori";}else{ $_SESSION["provenance"] = $HTTP_REFERER;}     }else{       $_SESSION["provenance"] = "Ici";     }     $_SESSION["userip"] = (getenv("HTTP_X_FORWARDED_FOR") ? getenv("HTTP_X_FORWARDED_FOR") : getenv("REMOTE_ADDR"));//     $_SESSION["index"] = $_SESSION["location"]."index_bis.php";      $_SESSION["index"] = $_SESSION["location"]."index.php";   }}include("sys.actions.php");?>